import React from 'react';
import  './contact.css';
import emailjs from 'emailjs-com';

export default function Contact() {
  function sendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm(process.env.REACT_APP_SERVICE_ID, process.env.REACT_APP_TEMPLATE_ID, e.target, process.env.REACT_APP_USER_ID)
      .then((result) => {
          window.location.reload()  //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
      });
  }
  return     <section id="contact" >
    <div className='about-title'>
							<span className='jump load-jump'>L</span>
							<span className='jump load-jump'>e</span>
							<span className='jump load-jump'>t</span>
              <span className='jump load-jump'>'</span>
							<span className='jump load-jump'>s</span>
              <span> </span>

							<span className='jump load-jump'>m</span>
							<span className='jump load-jump'>e</span>

						</div>
<form className="contact-form" onSubmit={sendEmail}>
      <input type="hidden" name="contact_number" />
      <label>Name</label>
      <input type="text" name="from_name" />
      <label>Email</label>
      <input type="email" name="from_email" />
      <label>Subject</label>
      <input type="text" name="subject" />
      <label>Message</label>
      <textarea name="html_message" />
      <input type="submit" value="Send" />
    </form>
    </section>
  ;
}
