import React, { Component } from 'react';
//import 'materialize-css/dist/css/materialize.min.css';
import { HashLink as Link ,NavHashLink} from 'react-router-hash-link';
import gmail from '../../images/gmailgreen48.png';
import gmailred from '../../images/gmailcolor48.png';
import './navbar.css';
import { withRouter } from 'react-router-dom'

import ScrollableSection, { ScrollableLink } from 'react-update-url-on-scroll';


export default  function Navbar (props) {

	const[path,setPath]=React.useState('hero');
	console.log(props)

	React.useEffect(() => props.history.listen(() => {
		setPath(props.location.hash)
		console.log(props)
        // do something on route change
        // for my example, close a drawer
    }), [props.location])

	console.log(path)
		return (
			<div className='navbar'>
				<div className='nav-content'>

	{console.log(props.location.pathname)}
					<Link to='/#about' id="nav-about" className={props.location.hash==('#about')?'nav-active':''} >
						About
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>					</Link>
					<Link to='#experiences' id="nav-experiences" className={props.active==='experiences'?'nav-active':''}>
						Project
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>					</Link>
					<Link to='#skills' id="nav-skills" className={props.active==='skills'?'nav-active':''}>
						Skills
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>
					</Link>
					<div className="navIcon">
						<a className="icon-link" href="https://www.facebook.com/daniella.aharon.7/" target="_blank"><i className="fab fa-facebook-f"></i></a>
						<a className="icon-link" href="https://www.linkedin.com/in/danielle-aharon-478b67199/" target="_blank"><i className="fab fa-linkedin-in"></i></a>
						<a className="icon-link" href="https://github.com/danielleaharon" target="_blank"><i className='fab fa-github fa-lg' aria-hidden="true"></i></a>

					</div>
				</div>

			</div>





		);

}
