import React from 'react';
import './contact.css';
import emailjs from 'emailjs-com';

import {
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,

} from "@material-ui/core";

export default function Contact({ showModal, handleClose }) {
  const [isEmptyEmail, setIsEmptyEmail] = React.useState('e-empty')
  const [isEmptyName, setIsEmptyName] = React.useState('n-empty')


  function sendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm(process.env.REACT_APP_SERVICE_ID, process.env.REACT_APP_TEMPLATE_ID, e.target, process.env.REACT_APP_USER_ID)
      .then((result) => {
        window.location.reload()
        handleClose() //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
      });
  }
  const isMailEmpty = (e) => {

    if (e.target.value !== '')
      setIsEmptyEmail('n-not-empty')
    else setIsEmptyEmail('n-empty')

  }
  const isNameEmpty = (e) => {

    if (e.target.value !== '')
      setIsEmptyName('e-not-empty')
    else setIsEmptyName('e-empty')

  }
  return <Dialog
    open={showModal}
    disableEscapeKeyDown
    aria-labelledby="dialog-title"
    aria-describedby="dialog-description"
    id="dialog-contact"
  >
    <div className='dialog-contact-page'>
      <div id="dialog-content-title">
        <div className='conact-title'>

          <span className='jump load-jump'>L</span>
          <span className='jump load-jump'>e</span>
          <span className='jump load-jump'>t</span>
          <span className='jump load-jump'>'</span>
          <span className='jump load-jump'>s</span>
          <span> </span>

          <span className='jump load-jump'>b</span>
          <span className='jump load-jump'>e</span>

          <span> </span>

          <span className='jump load-jump'>i</span>
          <span className='jump load-jump'>n</span>

          <span> </span>

          <span className='jump load-jump'>t</span>
          <span className='jump load-jump'>o</span>
          <span className='jump load-jump'>u</span>
          <span className='jump load-jump'>c</span>
          <span className='jump load-jump'>h</span>

        </div>    </div>
      <DialogContent>
        <DialogContentText id="dialog-description">

          <form className="contact-form" onSubmit={sendEmail}>
            <input type="hidden" name="contact_number" />

            {/* <div className='inputs-box'> */}
              <div className='input-box'>
                <input type="text" name="from_name" onChange={isNameEmpty} required="true" />
                <label className={isEmptyName}>Name</label>
                <span className="focus-border"></span>

              </div>
              <div className='input-box'>
                <input type="email" name="from_email" onChange={isMailEmpty} required="true" />
                <label className={isEmptyEmail}>Email</label>
                <span className="focus-border"></span>

              </div>
            {/* </div> */}
            <div className='input-box'>
              <input type="text" name="subject" required="true" />
              <label>Subject</label>
              <span className="focus-border"></span>

            </div>
            <div className='text-box'>
              <textarea name="html_message" required />
              <label>Message</label>
              <span className="focus-border">
                <i></i>
              </span>
            </div>
            <div className='btn-send'>
              <input type="submit" value='Send message!' />
              {/* <span className="btn-text">Send message!</span> */}
              <span className='arrow'>
                <i className="fas fa-angle-right"></i>
              </span>	      </div>
          </form>
        </DialogContentText>
      </DialogContent>
      <DialogActions>
      <div className='dialog-cotact-btn' onClick={handleClose}>
      <i className="fa fa-times"></i>    </div>

    </DialogActions>
    </div>
  </Dialog>


    // <section id="contact" className='px-60 page' >
    //   <div className='left'>

    //   </div>

    //   </section>
    ;
}
