import React from 'react';
import { useEffect, useState, useRef } from "react";
import {
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  Button,
  DialogTitle
} from "@material-ui/core";
import "./index.scss";

const langLogosArray = {
"Android studio": <i class="fab fa-android"></i> ,
"React" :<i class="fab fa-react"></i>
}

export default function DialogProject({showModal,handleClose,content}) {

const[seeDeomo,setSeeDemo]=React.useState(false);

  return (

      <Dialog
        open={showModal}
        disableBackdropClick
        disableEscapeKeyDown
        aria-labelledby="dialog-title"
        aria-describedby="dialog-description"
        id="dialog-project"
      >
        <div id="dialog-title">
           {content.title}
           <div className="project-img">
           <img src={ content.image}/>
           </div>
        </div>
        <DialogContent>
          <DialogContentText id="dialog-description">
            <div className='lang-logo'>
                <div>
        {langLogosArray[content.lang]}
          {content.lang}
          </div>
          <a className=' project-github' href={ content.github}> <i className='fab fa-github fa-lg'></i>
         <span> see on GitHub</span>
          </a>

          </div>
          <div>
          {content.description}
         {content.demo&& <div className='see-demo-btn' onClick={()=>setSeeDemo(!seeDeomo)}>See Demo</div>}
        {seeDeomo&& <div class="content-demo">{content.demo}</div>}
          </div>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <div className='project-btn' onClick={handleClose}>
            ok
          </div>
        </DialogActions>
      </Dialog>
  );
}
