import React, { Component } from 'react';
import Hero from '../../components/hero/hero';
import HeroReverse from '../../components/hero/heroReverse';
import Carousel from 'react-bootstrap/Carousel';
import Slider from "react-slick";
import Skills from '../../components/pages/skills/Skills';

import IndividualIntervalsExample from './individualIntervalsExample';
import Experiences from '../../components/pages/experiences/Experiences';
import Contact from '../../components/pages/Contact/Contact';

import Navbar from '../../components/navbar/Navbar';
import $ from 'jquery';

import Profile from '../../components/pages/profile/Profile';
import About from '../../components/pages/about/About';
import ScrollableSection, { ScrollableLink } from 'react-update-url-on-scroll';
import { configureAnchors } from 'react-update-url-on-scroll'




export default function Home(props) {
	const [state, setState] = React.useState(true);
	const [active, isActive] = React.useState('hero');
	const [lastActive, setLastActive] = React.useState(0);
	const slider = React.useRef(null);

	configureAnchors({scrollDelay: 100})
    const settings = {
		dots: false,
		arrows:false,
		infinite: false,
		speed: 500,
		slidesToShow: 1,
		slidesToScroll: 1,
		vertical:true,
		verticalSwiping:true,
		swipe:true,
		swipeToSlide:true,
		touchMove:true,
		waitForAnimate:true,
		// speed:5000,
        beforeChange: (current, next) => isActive( next ),
		afterChange: current => isActive( current )

	  };

	// React.useEffect(()=>{

	// 		var $body    = $('html, body'), // Define jQuery collection 
	// 			content  = $('#main').smoothState({
	// 			  onStart : {
	// 				duration: 250,
	// 				render: function () {
	// 				  content.toggleAnimationClass('is-exiting');

	// 				  // Scroll user to the top
	// 				  $body.animate({ 'scrollTop': 0 });

	// 				}
	// 			  }
	// 			}).data('smoothState');

	// },[])


	function checkScrollDirection(event) {
		document.body.removeEventListener("wheel", checkScrollDirection);
		if (state) {
			setState(false);

		if (checkScrollDirectionIsUp(event)) {
		  getSlider().slickPrev()

		} else {
		  getSlider().slickNext()


		}

			setTimeout(() => {
				setState(true);
				document.body.addEventListener('wheel', checkScrollDirection);

			}, 1200);

	}
	  }

	  function checkScrollDirectionIsUp(event) {
		if (event.wheelDelta) {
		  return event.wheelDelta > 0;
		}
		return event.deltaY < 0;
	  }
	  React.useEffect(() => {

	    function watchScroll() {
			var scrollableElement = document.body; //document.getElementById('scrollableElement');

			scrollableElement.addEventListener('wheel', checkScrollDirection,false);
			//  scrollableElement.removeEventListener("wheel", checkScrollDirection);

	    //   document.addEventListener("scroll", logit,false);
	    }
	    watchScroll();



	  },[state]);

	  const getSlider=()=>{
		  return slider.current;
	  }

	  React.useEffect(() => {

	    // function watchScroll() {

	    //   document.addEventListener("scroll", logit);
	    // }
	    // watchScroll();


		console.log(slider)

	  },[slider.current]);

	return (
		<div className="home">
			<Navbar active={active} getSlider={()=>getSlider()} { ...props} />
			{/* <div id='scroll-container' onScroll={()=>{console.log('fff')}} className='scroll-container'> */}
			<div className='carousel-div'>
			<Slider  ref={slider}  {...settings}>
				<div >
			<Hero active={active} />
			</div>
				<div>
					<About active={active} isActive={isActive} />

				</div>
				<div>
					<Skills active={active} isActive={isActive} />

				</div>
				<div>
					<Experiences active={active} isActive={isActive} />

				</div>

				<div>
					<Contact active={active} isActive={isActive} />

				</div>
		</Slider>
			{/* </div> */}
			</div>
		</div>

	);
}
