import React from 'react';
import Contact from '../../components/pages/Contact/Contact';

import './footer.scss';
import Bye from '../../images/me-contact.png';


import { HashLink as Link } from 'react-router-hash-link';


export default function Navbar(props) {

	const [showContactModel, setShowContactModel] = React.useState(false);





	return (
		<div className='footer area'>
<div className='footer-title'>
{		['T','h','a','n','k', ' ','Y','o','u'].map((item, index) => {
								if (item == " ")
									return <span key={index} className=''> </span>
								if (item == "br")
									return <br key={index}></br>
								return <span key={index} className=" jump" >	{item}</span>

							})}
                            </div>
            <div className='footer-img'>
                <img src={Bye}/>
            </div>
            <div className="footer-rows ">
<div className='btn-arrow' onClick={() => setShowContactModel(true)} >
    Contact Me

    <span className='arrow'>
        <i className="fas fa-angle-right"></i>
    </span>
</div>
</div>
<div className='Social-icons' style={{'--txtcolor':props.txtcolor}}>

				<div className="link">
					<a className="icon-link" rel="noopener noreferrer" href="https://www.facebook.com/daniella.aharon.7/" target="_blank"><i className="fab fa-facebook-f"></i></a>

				</div>
				<div className="link">
					<a className="icon-link" rel="noopener noreferrer" href="https://www.linkedin.com/in/danielle-aharon-478b67199/" target="_blank"><i className="fab fa-linkedin-in"></i></a>
				</div>
				<div className="link">				<a className="icon-link" rel="noopener noreferrer" href="https://github.com/danielleaharon" target="_blank"><i className='fab fa-github fa-lg' aria-hidden="true"></i></a>
				</div>
			</div>
        </div>



	);

}
