import React, { Component } from 'react';
//import 'materialize-css/dist/css/materialize.min.css';


import './tetrisBgAnimation.css';
import { TETROMINOS } from '../tetris/tetrominos';

import CellPreview from '../tetris/components/CellPreview';


export default function TetrisBgAnimation(props) {
	 const randomTetromino = () => {
		const tetrominos = 'IJLOSTZ';
		const randTetromino =
		tetrominos[Math.floor(Math.random() * tetrominos.length)];
		return randTetromino;
	};

	return (
		<div  className='tetrisBgAnimation'  >
			  <div className='row1'>
                {[...Array(40).keys()].map((item,index)=>{
					const rand=randomTetromino();
                  return  <CellPreview size={Math.floor(Math.random() * (80 - 20) + 20)+'px'} delay={Math.floor(Math.random() * 4)+'s'} left={Math.floor(Math.random() * 100)+'%'} key={index} type={rand} text={''} shape={TETROMINOS[rand].shape} />
                                }                )}
              </div>
		   {/* <div className='row1'>
                {['L','O','S','T','Z','I','J'].map((item,index)=>{
                  return  <CellPreview size={'80px'} key={index} type={item} text={TETROMINOS[item].text} shape={TETROMINOS[item].shape} />
                                }                )}
              </div>
			  <div className='row2'>
                {['L','O','S','T','Z','I','J'].map((item,index)=>{
                  return  <CellPreview size={'80px'} key={index} type={item} text={TETROMINOS[item].text} shape={TETROMINOS[item].shape} />
                                }                )}
              </div> */}
		</div>
	);

}
