import React from 'react';
import Hero from '../../components/hero/hero';

import Slider from "react-slick";
import anime from 'animejs/lib/anime.es.js';

import Experiences from '../../components/pages/experiences/Experiences';


import Navbar from '../../components/navbar/Navbar';
import $ from 'jquery';

import About from '../../components/pages/about/About';
// import ScrollableSection, { ScrollableLink } from 'react-update-url-on-scroll';
// import { configureAnchors } from 'react-update-url-on-scroll'




export default function Home({ active, isActive }) {
	const [state, setState] = React.useState(7.1);
	const [classActive, setClassActive] = React.useState("");
	const [nextColorRigth, SetNextColorRigth] = React.useState("#FFB5A9");
	const [nextColorLeft, SetNextColorLeft] = React.useState("#1C1C1C");


	React.useEffect(() => {
		var boxes = document.querySelectorAll('.slice');
		document.addEventListener('scroll', clickHandler,{ passive: true });
		// boxes.forEach(function (el) {
		// 	if (el.addEventListener) {
		// 		el.addEventListener('click', clickHandler);
		// 	} else {
		// 		el.attachEvent('onclick', clickHandler);
		// 	}
		// })

		var detectOverlap = (function () {
			function getPositions(elem) {
				var pos = elem.getBoundingClientRect();
				return [[pos.left, pos.right], [pos.top, pos.bottom]];
			}

			function comparePositions(p1, p2) {
				var r1, r2;
				if (p1[0] < p2[0]) {
					r1 = p1;
					r2 = p2;
				} else {
					r1 = p2;
					r2 = p1;
				}
				console.log(r1)
				console.log(r2)

				return r1[1] < r2[0] || r1[0] === r2[0];
			}

			return function (a, b) {
				var pos1 = getPositions(a),
					pos2 = getPositions(b);
					// console.log(pos1)
					// console.log(pos2)

				return comparePositions(pos1[0], pos2[0]) && comparePositions(pos1[1], pos2[1]);
			};
		})();

		function clickHandler(e) {

			var elem = document.getElementById('navbar'),
				elems = document.querySelectorAll('.slice'),
				elemList = Array.prototype.slice.call(elems),
				touching = [];
				var elem2 = document.getElementById('about');

			for (var i = 0; i < elemList.length; i++) {
				if (detectOverlap(elem, elemList[i])) {
					touching.push(elemList[i].id);
				}
			}
			if (touching.length) {
				console.log(elem.id + ' touches ' + touching.join(' and ') + '.');
			} else {
				console.log(elem.id + ' touches nothing.');
			}

			// elem.classList.forEach((c)=>{

			// 	if(c.includes('top'))
			// 	elem.classList.remove(c.toString());

			// })
			// if(document.documentElement.scrollTop>495 && document.documentElement.scrollTop<600){
			// 	elem.classList.add('top-'+500)

			// }
			// else elem.classList.add('top-'+document.documentElement.scrollTop)
			var elem3 = document.getElementById('nav-bg');

			elem3.style.transform='rotate('+state-1+'deg)';
			setState(state-1);


		}
	})
	return (
		<div className="home">

			{/* {'hero'+classActive==='hero-reverse' || active===1?<div className='about-rev-helf-bg'></div>:''} */}


			<Navbar active={active} getSlider={() => console.log()} />
			<div className={'carousel-div active-' + active + classActive}>
				{/* <div className='all-bg'>

				{active+classActive=='0-reverse'&&<TetrisBgAnimation/>}


			</div> */}
				{/* <div className='helf-midel-bg' style={{'--mid-color':nextColorRigth}}></div>

			<div className='helf-rigth-bg' style={{'--rigth-color':nextColorRigth}}></div>
						<div className='helf-left-bg' style={{'--left-color':nextColorLeft}}></div> */}

				<div className="page" >
					<Hero active={active} class={classActive} />
				</div>
				<div className="page">
					<About active={active} isActive={isActive} class={classActive} slickGoTo={() => console.log()} />

				</div>
				{/* <div>
					<Skills active={active} isActive={isActive} />

				</div> */}
				<div className="page">
					<Experiences active={active} isActive={isActive} class={classActive} />

				</div>


				{/* </div> */}

			</div>
		</div>

	);
}
