import React from 'react';
import cv from '../../../images/CVDanielleAharon.pdf';
import ImgProfile from '../../../images/myimage.png';
import  './about.css';


export default function About(props) {
	const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    // Loading function to load data or 
    // fake it using setTimeout;
    const loadData = async () => {

      // Wait for two second
      await new Promise((r) => setTimeout(r, 1000));

      // Toggle loading state
      setLoading((loading) => !loading);
    };

    loadData();
  }, [props.active])
  if (loading) {
    return <div>Loading....</div>
}

  return (
    <section id="about" className={props.active==='about'?'active':''} >
 {props.active==='about'&& <div className='about-helf-bg'></div>}
     <div>
       <h1>About Me</h1>
       <p> dddddd dddddd dddddd</p>
     </div>
     <div>
       <img src={ImgProfile}></img>
     </div>

    </section>
  );
}
