import React, { Component } from 'react';
import Hero from '../../components/hero/hero';
import Navbar from '../../components/navbar/Navbar';
import $ from 'jquery';

import Profile from '../../components/pages/profile/Profile';
import About from '../../components/pages/about/About';
import Skills from '../../components/pages/skills/Skills';
import Experiences from '../../components/pages/experiences/Experiences';



export default function Home() {
	const [scrollY, setScrollY] = React.useState(0);

	function isInViewport(element) {
		const rect = element.getBoundingClientRect();
		return (
			rect.top >= 0 &&
			rect.left >= 0 &&
			rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
			rect.right <= (window.innerWidth || document.documentElement.clientWidth)
		);
	}
function logit() {
	console.log('logit')
	$('.scroll-container section').each(function() {
		console.log($(this));

		// if($(this).visible())
		//   console.log($(this).attr('id'));
	  });
    setScrollY( document.getElementById('scroll-container').pageYOffset);
	$.fn.isInViewport = function() {
		var elementTop = $(this).offset().top;
		var elementBottom = elementTop + $(this).outerHeight();

		var viewportTop = $(window).scrollTop();
		var viewportBottom = viewportTop + $(window).height();

		return elementBottom > viewportTop && elementTop < viewportBottom;
	  };

	  $(window).on('resize scroll', function() {
		$('.color').each(function() {
			var activeColor = $(this).attr('id');
		  if ($(this).isInViewport()) {
			$('#fixed-' + activeColor).addClass(activeColor + '-active');
		  } else {
			$('#fixed-' + activeColor).removeClass(activeColor + '-active');
		  }
		});
	  });
  }

  React.useEffect(() => {
    function watchScroll() {
      document.getElementById('scroll-container').addEventListener("scroll", logit);
    }
    watchScroll();
    return () => {
		document.getElementById('scroll-container').removeEventListener("scroll", logit);
    };
  },[]);
		return (
			<section className="home">

				<Navbar/>
				<div id='scroll-container' className='scroll-container'>
				<Hero />
				<About/>
				</div>

			</section>

		);
	}
