import React from 'react';
import Hero from '../../components/hero/hero';

import Slider from "react-slick";
import anime from 'animejs/lib/anime.es.js';

import Experiences from '../../components/pages/experiences/Experiences';


import Navbar from '../../components/navbar/Navbar';
import $ from 'jquery';

import About from '../../components/pages/about/About';
// import ScrollableSection, { ScrollableLink } from 'react-update-url-on-scroll';
// import { configureAnchors } from 'react-update-url-on-scroll'




export default function Home({active,isActive}) {
	const [state, setState] = React.useState(true);
	const [classActive, setClassActive] = React.useState("");
	const [nextColorRigth, SetNextColorRigth] = React.useState("#FFB5A9");
	const [nextColorLeft, SetNextColorLeft] = React.useState("#1C1C1C");
	const [sliderCurrent, setSliderCurrent] = React.useState(0);

	const slider = React.useRef(null);
	const colorMap=['#1C1C1C','#FFB5A9','#1C1C1C'];
	const colorMapLeft=['#1C1C1C','#1C1C1C','#619B97'];
    const settings = {
		dots: false,
		arrows:false,
		infinite: false,
		swipe:false,
		speed: 0,
		slidesToShow: 1,
		slidesToScroll: 1,
		fade:true,
		// vertical:true,
		verticalSwiping:true,
		swipeToSlide:false,
		touchMove:false,
		waitForAnimate:true,
		easing:false,
		vertical:true,
		accessibility:false,
		// speed:5000,
        beforeChange: (current, next) => {
			// setSliderCurrent(next)
			beforeChangeCircle(current,next)
		}
		,
		afterChange: current => {isActive( current );
			SetNextColorRigth(colorMap[current+1]); SetNextColorLeft(colorMapLeft[current+1])
			// setSliderCurrent(current)

			// beforeChangeCircle(current,current)
			// setTimeout(() => {
			// 	SetNextColorRigth(colorMap[current+1]); SetNextColorLeft(colorMapLeft[current+1])

			// }, 2000);



		}

	  };


	  const beforeChangeCircle=(current, next)=>{

		$('.page:eq('+ next +')').css('z-index', parseInt($(this).css('z-index')) + 1);
		$('.page:eq('+ next +')').css('clip-path', 'circle(0% at 968px 240px)');
	    anime({
			targets: $('.page')[next],
			duration: 3000,
			update: function(anim) {
			  $('.page:eq('+ next +')').css('clip-path', 'circle('+ (anim.progress*2) +'% at  968px 240px');
			}
		  });
	  }
	React.useEffect(()=>{

		if(getSlider())
		getSlider().slickGoTo(3)

		// $('.page').each(function(i,e){
		// 	$(this).click(function(event){
		// 	  var x = event.pageX;
		// 	  var y = event.pageY;

		// 	  var nextItem = i + 1;
		// 	  if (nextItem >= $('.page').length){
		// 		nextItem = 0;
		// 	  }

			//   $('.page:eq('+ nextItem +')').css('z-index', parseInt($(this).css('z-index')) + 1);
			//   $('.page:eq('+ nextItem +')').css('clip-path', 'circle(0% at '+ x +'px '+ y +'px)');

			//   anime({
			// 	targets: $('.page')[nextItem],
			// 	update: function(anim) {
			// 	  $('.page:eq('+ nextItem +')').css('clip-path', 'circle('+ (anim.progress*2) +'% at '+ x +'px '+ y +'px)');
			// 	}
			//   });
		// 	});
		//   });
			// var $body    = $('html, body'), // Define jQuery collection 
			// 	content  = $('#main').smoothState({
			// 	  onStart : {
			// 		duration: 250,
			// 		render: function () {
			// 		  content.toggleAnimationClass('is-exiting');

			// 		  // Scroll user to the top
			// 		  $body.animate({ 'scrollTop': 0 });

			// 		}
			// 	  }
			// 	}).data('smoothState');

	},[])


	function checkScrollDirection(event) {
		document.body.removeEventListener("wheel", checkScrollDirection);
		if (state) {
			setState(false);

		if (checkScrollDirectionIsUp(event)) {
			setClassActive('')
			setTimeout(() => {
				getSlider().slickPrev()


			}, 1000);

		} else {
			setClassActive('-reverse');
			// beforeChangeCircle(sliderCurrent,sliderCurrent+1)

			setTimeout(() => {

				getSlider().slickNext();

			}, 1200);

			setTimeout(() => {
				setClassActive('')
				// beforeChangeCircle(sliderCurrent,sliderCurrent+1)


			}, 3200);


		}

			setTimeout(() => {
				setState(true);
				document.body.addEventListener('wheel', checkScrollDirection);

			}, 1200);

	}
	  }

	  function checkScrollDirectionIsUp(event) {
		if (event.wheelDelta) {
		  return event.wheelDelta > 0;
		}
		return event.deltaY < 0;
	  }
	  React.useEffect(() => {

	    function watchScroll() {
			var scrollableElement = document.body; //document.getElementById('scrollableElement');
			scrollableElement.addEventListener('wheel', checkScrollDirection,false);

	    }
	    watchScroll();



	  },[]);

	  const getSlider=()=>{
		  return slider.current;
	  }



	return (
		<div className="home page">

						{/* {'hero'+classActive==='hero-reverse' || active===1?<div className='about-rev-helf-bg'></div>:''} */}


			<Navbar active={active} getSlider={()=>getSlider()} />
			<div className={'carousel-div active-'+active+classActive}>
			{/* <div className='all-bg'>

				{active+classActive=='0-reverse'&&<TetrisBgAnimation/>}


			</div> */}
			{/* <div className='helf-midel-bg' style={{'--mid-color':nextColorRigth}}></div>

			<div className='helf-rigth-bg' style={{'--rigth-color':nextColorRigth}}></div>
						<div className='helf-left-bg' style={{'--left-color':nextColorLeft}}></div> */}

			<Slider  ref={slider}  {...settings}>
				<div >
			<Hero active={active} class={classActive} />
			</div>
				<div>
					<About active={active} isActive={isActive} class={classActive} />

				</div>
				{/* <div>
					<Skills active={active} isActive={isActive} />

				</div> */}
				<div className="page">
					<Experiences active={active} isActive={isActive}class={classActive} />

				</div>


		</Slider>
			{/* </div> */}

			</div>
		</div>

	);
}
