		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ4 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}

	return (
		<div id="scroll-container-intro" className="section intro scroll-container">
			<DialogProject showModal={showModalProject} handleClose={() => { setShowModalProject(false) }} content={modalProjectContent} />

			<div className='area'>
				<Navbar bgcolor="aquamarine" />
				<div className='intro-contact '>
