import React from 'react';
//import 'materialize-css/dist/css/materialize.min.css';

import './hero.scss';
import ImgProfile from '../../images/comic-me.png';
import ImgProfileBG from '../../images/ofiice2.png';
import Draw from './draw';

const colors=['#CC99FF','#A9D1F7','#B4F0A7','#FFFFBF','#FFDFBE','#FFB1B0']

export default function Hero(props) {
	React.useEffect(() => {
		var i = 1;

		// const element = document.getElementById('hover-msg');

		// element.addEventListener("animationend", () => {
		// 	// Set your final state here. For example:
		// 	element.style["opacity"] = 0;
		// }, { once: true });
		// $('.load-jump').each(function() {
		// 	// $(this).removeClass('load-jump');
		// 	setTimeout(()=>{
		// 		$(this).removeClass('load-jump');

		// 		$(this).addClass('load-jump2');

		// 	},0+i)
		// 	setTimeout(()=>{
		// 		$(this).removeClass('load-jump2');

		// 	},1000+i)
		// 	i=i+200;
		// })
	}, [])

	const hoverLetter = () => {
		console.log('hover')
		// const element = document.getElementById('hover-msg');
		// element.style["opacity"] = 0;

	}


	const startCornerHoverQ1 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(0.99,0,0.14,0.000,-0.2007,0.9,0.14,0,-0.13,-0.14,0.9,0,0,0,0,1);');

	}
	const EndCornerHoverQ1 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}
	const startCornerHoverQ2 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ2 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}

	const startCornerHoverQ3 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ3 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}
	const startCornerHoverQ4 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ4 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}

	return (
		<div id='hero-section'  className='hero'>
				<div className='content slice' >
					<div className='content-div not-safari'>
						<div className='max-div'>
						{
							['h','i',','].map((item,index)=>{
								let color=colors[index%6];
								if(item==" ")
								return 	<span key={index} className=''> </span>
								if(item=="br")
								return <br key={index}></br>
								if(item=="," || item=="'")
								return <span key={index} className=" jump load-jump jump-comaa" style={{'--textcolor':color}}>	{item}</span>
								return <div className="clip clip-letter">{item}<Draw callback={hoverLetter} lineColor={color} letter={item} font="Luckiest Guy" /></div>

							})
						}

						</div>

						<div className='hover-msg'>
							{/* <div id="hover-msg" className="msg">Try Hover Letters!</div>
							<div className="clip clip-letter ">I</div>
							<span className='jump load-jump '>'</span>
							<div className="clip clip-letter">m</div> */}

							{['i',"'",'m','d','a','n','i','e','l','l','e',','].map((item,index)=>{
								let color=colors[index%6];
								if(item==" ")
								return 	<span key={index} className=''> </span>
								if(item=="br")
								return <br key={index}></br>
								if(item=="," || item=="'")
								return <span key={index} className=" jump load-jump jump-comaa" style={{'--textcolor':color}}>	{item}</span>
								return <div className="clip clip-letter">{item}<Draw callback={hoverLetter} lineColor={color} letter={item} font="Luckiest Guy" /></div>

							})
						}


						</div>

						<div className='web-dev'>

						{/* {
							['w','e','b',' ','d','e','v','e','l','o','p','e','r'].map((item,index)=>{
								let color=colors[index%6];
								if(item==" ")
								return 	<span className=''> </span>
								if(item=="br")
								return <br></br>
								if(item=="," || item=="'")
								return <div className=" jump load-jump jump-comaa" style={{'--textcolor':color}}>	{item}</div>
								return<div className=" jump load-jump ff-os" style={{'--textcolor':color}}>	{item}</div>

							})
						} */}

							<div className="clip clip-letter ">W<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='W' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">E<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='E' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">B<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='B' font="Luckiest Guy" /></div>

							<span> </span>
							<div className="clip clip-letter ">D<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='D' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">E<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='E' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">V<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='V' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">E<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='E' font="Luckiest Guy" /></div>
							<div className="clip clip-letter">L<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='L' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">O<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='O' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">P<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='P' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">E<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='E' font="Luckiest Guy" /></div>
							<div className="clip clip-letter ">R<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='R' font="Luckiest Guy" /></div>
						</div>


					</div>
					<div className='content-div safari'>
						{
							['h','i',',','br', 'i',"'",'m',' ','d','a','n','i','e','l','l','e',',','br','w','e','b',' ','d','e','v','e','l','o','p','e','r'].map((item,index)=>{
								let color=colors[index%6];
								if(item==" ")
								return 	<span key={index} className=''> </span>
								if(item=="br")
								return <br key={index}></br>
								if(item=="," || item=="'")
								return <span key={index} className=" jump load-jump jump-comaa" style={{'--textcolor':color}}>	{item}</span>
								return<span key={index} className=" jump load-jump" style={{'--textcolor':color}}>	{item}</span>

							})
						}

					</div>
					<span className='block'></span>


				</div>



		</div>
	);

}
