import React from 'react';
import { useEffect, useRef, useState } from "react"; 


// import "./App.css"; 


function Draw(props) { 

  const canvasRef = useRef(null); 

  const ctxRef = useRef(null); 

  const [isDrawing, setIsDrawing] = useState(false); 

  const [lineWidth, setLineWidth] = useState(25); 

  const [lineColor, setLineColor] = useState(props.lineColor); 

  const [lineOpacity, setLineOpacity] = useState(1); 
  const [letter, setLetter] = useState(props.letter); 



  // Initialization when the component 

  // mounts for the first time 

  useEffect(() => { 

    const canvas = canvasRef.current; 

    const ctx = canvas.getContext("2d"); 

    ctx.lineCap = "round"; 

    ctx.lineJoin = "round"; 

    ctx.globalAlpha = lineOpacity; 

    ctx.strokeStyle = lineColor; 

    ctx.lineWidth = lineWidth; 

    ctxRef.current = ctx; 

  }, [lineColor, lineOpacity, lineWidth]); 



  // Function for starting the drawing 

  const startDrawing = (e) => { 


    props.callback&& props.callback()


    ctxRef.current.beginPath(); 

    ctxRef.current.moveTo( 

      e.nativeEvent.offsetX,  

      e.nativeEvent.offsetY 

    ); 

    setIsDrawing(true); 

  }; 



  // Function for ending the drawing 

  const endDrawing = () => { 

    ctxRef.current.closePath(); 

    setIsDrawing(false); 

  }; 



  const draw = (e) => { 

    if (!isDrawing) { 

      return; 

    } 

    ctxRef.current.lineTo( 

      e.nativeEvent.offsetX,  

      e.nativeEvent.offsetY 

    ); 



    ctxRef.current.stroke(); 

  }; 



  return ( 

    // <div className="App-draw"> 

      <div className="App-draw draw-area"> 


        <canvas 

          onMouseDown={startDrawing} 

          onMouseUp={endDrawing} 

          onMouseMove={draw} 
          onMouseOver={startDrawing}

          ref={canvasRef} 

          width={`110pxx`} 

          height={`110px`} 
          style={{'--clip-id':"URL(#svgTextPath"+letter+')'}}

        >
      </canvas> 
      <svg height="0" width="0">
    <defs>
        <clipPath id={"svgTextPath"+letter}>
            <text x="2" y="107" lengthAdjust="spacing" fontFamily={props.font} fontSize="70px" fontWeight="400"> {letter} </text>
        </clipPath>
    </defs>
</svg>

    </div> 

  ); 
} 



export default Draw;
