import React from 'react';
import  './about.scss';
import Tetris from '../../tetris/tetris';
import TetrisBgAnimation from '../../TetrisBgAnimation/tetrisBgAnimation';
import Logo from '../../navbar/Logo';

const activeColors = ['antiquewhite', '#7cd3c7', '#DFE0DF'];

export default function About(props) {
	const [loading, setLoading] = React.useState(true);
	React.useEffect(() => {
		var i = 1;

    if(props.active===1){
		const element = document.getElementById('all-bg');
    setTimeout(()=>{
      element.style["z-index"] = -1;

    },5000)
  }else{
    const element = document.getElementById('all-bg');
    element.style["z-index"] = 1;

  }
      // element.addEventListener("animationend", () => {
      //   console.log(element)
      //   // Set your final state here. For example:
      //   element.style["z-index"] = -1;


      // }, { once: true });



	}, [props.active])

  return (
    <>
    <section  >

     <div className='left about-content slice'>
     <div className='skills-rigth px-60 w-100'>
        <div className='skill-lines'>
          <div className='skill'>Front-end</div>
          <div className='skill'>Back-end</div>
          <div className='skill'>ReactJS</div>
        </div>
        <div className='exp'>
          <div className='exp-card'>
            <div className='card-title'>Full stack developer</div>
            <div className='company'>4Legs</div>
            <div className='time'>2020-2021</div>
            <div className='description'>Building and maintaining the website on the server side and on the client side, dealing with a database</div>

          </div>
          <div className='exp-card'>
          <div className='card-title'>Frontend developer</div>
            <div className='company'>JFrog</div>
            <div className='time'>2021-present</div>
            <div className='description'>Website maintenance on the server side and on the client side, use of wordprees</div>
            </div>
        </div>
        </div>
        <div className='bg'></div>

     </div>

     <div className='rigth about-rigth slice'>

<Tetris active={ props.active}/>

     </div>
     <div className='bg-rigth'></div>


    </section>
    </>
  );
}
