import React from 'react';
import Contact from '../../components/pages/Contact/Contact';
import './navbar.css';



export default function Navbar(props) {

	const [path, setPath] = React.useState('')
	const [showContactModel, setShowContactModel] = React.useState(false)

	React.useEffect(() => {

		const onLocationChange = () => {

			setPath(window.location.hash)
		}
		window.addEventListener('popstate', onLocationChange);
		return () => {
			window.removeEventListener('popstate', onLocationChange)
		}
	}, [])

	const slickGoTo = (value) => {
		if (props.getSlider())
			props.getSlider().slickGoTo(value)
	}

	return (
		<>
			<Contact showModal={showContactModel} handleClose={() => { setShowContactModel(false) }} />
			<div className='navbar d-none d-md-block '>
				<div className='nav-content '>

					<div onClick={() => props.active !== 1 && slickGoTo(1)} id="nav-about" className={props.active == 1 ? 'nav-active nav-row' : 'nav-row'} >
						About
						<span className='arrow'>
							<i className="fas fa-angle-right"></i>
						</span>					</div>

					<div onClick={() => props.active !== 2 && slickGoTo(2)} id="nav-project" className={props.active === 2 ? 'nav-active nav-row' : 'nav-row'}>
						Work
						<span className='arrow'>
							<i className="fas fa-angle-right"></i>
						</span>					</div>
					<div onClick={() => setShowContactModel(true)} id="nav-contact" className='nav-row'>
						Contact
						<span className='arrow'>
							<i className="fas fa-angle-right"></i>
						</span>					</div>
					<div className="navIcon">
						<a className="icon-link" href="https://www.facebook.com/daniella.aharon.7/" target="_blank"><i className="fab fa-facebook-f"></i></a>
						<a className="icon-link" href="https://www.linkedin.com/in/danielle-aharon-478b67199/" target="_blank"><i className="fab fa-linkedin-in"></i></a>
						<a className="icon-link" href="https://github.com/danielleaharon" target="_blank"><i className='fab fa-github fa-lg' aria-hidden="true"></i></a>

					</div>
				</div>

			</div>
			<div className='navbar d-md-none'>
			<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">Navbar</a>
  <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup" aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
    <span class="navbar-toggler-icon"></span>
  </button>
  <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
    <div class="navbar-nav">
      <a class="nav-item nav-link active" href="#">Home <span class="sr-only">(current)</span></a>
      <a class="nav-item nav-link" href="#">Features</a>
      <a class="nav-item nav-link" href="#">Pricing</a>
      <a class="nav-item nav-link disabled" href="#">Disabled</a>
    </div>
  </div>
</nav>
</div>

		</>



	);

}
