import React, { Component } from 'react';
//import 'materialize-css/dist/css/materialize.min.css';
import { HashLink as Link ,NavHashLink} from 'react-router-hash-link';
import gmail from '../../images/gmailgreen48.png';
import gmailred from '../../images/gmailcolor48.png';
import './navbar.css';



export default  function Navbar (props) {

	const[path,setPath]=React.useState('')

React.useEffect(()=>{

	const onLocationChange=()=>{

		setPath(window.location.hash)
	}
	window.addEventListener('popstate', onLocationChange);
	  return()=>{
		window.removeEventListener('popstate',onLocationChange)
	  }
},[])

const slickGoTo=(value)=>{
	if(props.getSlider())
	props.getSlider().slickGoTo(value)
}

		return (
			<div className='navbar '>
				<div className='nav-content '>

					<div  onClick={()=>slickGoTo(1)} id="nav-about" className={props.active==1?'nav-active nav-row':'nav-row'} >
						About
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>					</div>
						<div onClick={()=>slickGoTo(2)} id="nav-skills" className={props.active===2?'nav-active nav-row':' nav-row'}>
						Skills
						<span className='arrow'>
							<i class="fas fa-angle-ri"></i>
						</span>
					</div>
					<div onClick={()=>slickGoTo(3)} id="nav-experiences" className={props.active===3?'nav-active nav-row':'nav-row'}>
						Work
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>					</div>
						<div onClick={()=>slickGoTo(4)} id="nav-contact" className={props.active===4?'nav-active nav-row':'nav-row'}>
						Contact
						<span className='arrow'>
							<i class="fas fa-angle-down"></i>
						</span>					</div>
					<div className="navIcon">
						<a className="icon-link" href="https://www.facebook.com/daniella.aharon.7/" target="_blank"><i className="fab fa-facebook-f"></i></a>
						<a className="icon-link" href="https://www.linkedin.com/in/danielle-aharon-478b67199/" target="_blank"><i className="fab fa-linkedin-in"></i></a>
						<a className="icon-link" href="https://github.com/danielleaharon" target="_blank"><i className='fab fa-github fa-lg' aria-hidden="true"></i></a>

					</div>
				</div>

			</div>





		);

}
