import React from 'react';
import { StyledStage } from '../styles/StyledStage';

import Cell from './Cell';

const StageMain = ({ stage,newGame }) => (

   <StyledStage width={stage[0].length} height={stage.length} id="stage">
      {stage.map(row => row.map((cell, x) => {
      // console.log(cell)
      return <Cell key={x} type={cell[0]} text={cell[2]} />}))}
    </StyledStage>
);
const Stage = ({ stage }) => (
    <StyledStage width={stage[0].length} height={stage.length} id="stage">
      {stage.map(row => row.map((cell, x) => {
      // console.log(cell)
      return <Cell key={x} type={cell[0]} text={cell[2]} />}))}
    </StyledStage>
  );
const StageStartPositon = ({ stage }) => (
    <StyledStage width={stage[0].length} height={stage.length} id="stage">
      {stage.map(row => row.map((cell, x) => {
      // console.log(cell)
      return <Cell key={x} type={cell[0]} text={cell[2]} />}))}
    </StyledStage>
  );

export default Stage;
