import React from "react";
import classnames from "classnames";
import pokeball from "../../images/memory-game.png";
import "./card.scss";

const Card = ({ onClick, card, index, isInactive, isFlipped, isDisabled,setShowModalProject }) => {
    const[opacity,setOpacity]=React.useState(false)
  const handleClick = () => {
    !isFlipped && !isDisabled &&  onClick(index);

  };
  const handleClickInActive = () => {
     isInactive && !isDisabled && setShowModalProject(true);

  };
  return (
    <div 
      className={classnames("card", {
        "is-flipped": isFlipped,
        "is-inactive": isInactive,
        [card.type]:isInactive,
        opacity:opacity

      })}
      onClick={isInactive?handleClickInActive:handleClick}
    >
      <div className="card-face card-font-face">
        <img src={pokeball} alt="pokeball" />
      </div>
      <div className="card-face card-back-face">
        <img src={card.image} alt="pokeball" />
      </div>
    </div>
  );
};

export default Card;
