import styled from 'styled-components';
// BG Image

export const StyledTetrisWrapper = styled.div`
  width: 100%;
  height: 500px;
//   background-size: cover;
  overflow: hidden;
`;

export const StyledTetris = styled.div`
  display: flex;
  align-items: flex-start;
  padding: 0px;
  margin: 0 auto;
  height: 50%;
//   max-width: 900px;

  aside {
    width: 100%;
    max-width: 200px;
    display: block;
    padding: 0 20px;
  }
`;
