import React from 'react';
import { StyledCell } from '../styles/StyledCellPreview';
import { TETROMINOS } from '../tetrominos';

// React.memo makes sure we only re-render the changed cells
const CellPreview = ({ type,text,shape }) => (
  <div className='preview-cell'>
    {shape.map((item,index)=>{

      return   <div >{item.reverse().map((cube, indexCube)=>{
        return <StyledCell type={cube}   color={cube!==0&&TETROMINOS[cube].color} title={item!==0&&text[index][indexCube]}>
        </StyledCell>
      })}
   </div>
    }  

)}
</div>
);

export default React.memo(CellPreview);
