import React from 'react';
import {
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,

} from "@material-ui/core";
import "./dialog.scss";



export default function DialogProjectFinish({ showModal, handleClose, handleRestart, moves, bestScore }) {


  return (
    <Dialog
      open={showModal}
      disableEscapeKeyDown
      aria-labelledby="dialog-title"
      aria-describedby="dialog-description"
      id="dialog-project-finish"
    >
      <div className='dialog-project-start-page'>
        {
          (bestScore < moves) && <div id="dialog-title"><span>WOW!</span>
            <br /> you finish the game
          </div>

        }
        {
          (bestScore == moves) && <div id="dialog-title">
            <span>WOW!</span>
            <br /> you finish the game
          </div>
        }  {

          (bestScore > moves) &&

          <div id="dialog-title">
            <span>WOW! You broke your best score!</span>

            <br /> you finish the game
          </div>

        }

        <DialogContent>
          <DialogContentText id="dialog-description">
            {
              (bestScore < moves) && <div>
                This time it didn't go well for you. your best score is {bestScore}.<br />
                and you did it on {moves} moves;
              </div>


            }
            {
              (bestScore == moves) && <div>

                You were so close to breaking your best score {bestScore}.<br />
                maybe try again? you did it on {moves} moves;
              </div>

            }  {

              (bestScore > moves) && <div>
                You broke your best score!<br />
                you did it for only {moves} moves
              </div>

            }


          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <div className='project-btn start' onClick={handleRestart}>
            New Game!
            <span className='arrow'>
              <i className="fas fa-angle-right"></i>
            </span>
          </div>
          <div className='project-btn later' onClick={handleClose}>
            maybe later
            <span className='arrow'>
              <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" fill="currentColor" className="bi bi-emoji-frown" viewBox="0 0 19 19">
                <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                <path d="M4.285 12.433a.5.5 0 0 0 .683-.183A3.498 3.498 0 0 1 8 10.5c1.295 0 2.426.703 3.032 1.75a.5.5 0 0 0 .866-.5A4.498 4.498 0 0 0 8 9.5a4.5 4.5 0 0 0-3.898 2.25.5.5 0 0 0 .183.683zM7 6.5C7 7.328 6.552 8 6 8s-1-.672-1-1.5S5.448 5 6 5s1 .672 1 1.5zm4 0c0 .828-.448 1.5-1 1.5s-1-.672-1-1.5S9.448 5 10 5s1 .672 1 1.5z" />
              </svg>			</span>
          </div>
        </DialogActions>
      </div>
    </Dialog>
  );
}
