import React from 'react';
import  './contact.css';
import emailjs from 'emailjs-com';

import {
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  Button,
  DialogTitle
} from "@material-ui/core";

export default function Contact(props) {
  const[isEmptyEmail, setIsEmptyEmail]=React.useState('e-empty')
  const[isEmptyName, setIsEmptyName]=React.useState('n-empty')


  function sendEmail(e) {
    e.preventDefault();    //This is important, i'm not sure why, but the email won't send without it

    emailjs.sendForm(process.env.REACT_APP_SERVICE_ID, process.env.REACT_APP_TEMPLATE_ID, e.target, process.env.REACT_APP_USER_ID)
      .then((result) => {
          window.location.reload()  //This is if you still want the page to reload (since e.preventDefault() cancelled that behavior) 
      }, (error) => {
      });
  }
  const isMailEmpty=(e)=>{

    if(e.target.value!=='')
    setIsEmptyEmail('n-not-empty')
    else setIsEmptyEmail('n-empty')

  }
  const isNameEmpty=(e)=>{

    if(e.target.value!=='')
    setIsEmptyName('e-not-empty')
    else setIsEmptyName('e-empty')

  }
  return    
  <Dialog
  open={showModal}
  disableBackdropClick
  disableEscapeKeyDown
  aria-labelledby="dialog-title"
  aria-describedby="dialog-description"
  id="dialog-project-start"
>
  <div className='dialog-project-start-page'>
    <div id="dialog-title">
      Hi!,<br /> Wanna play <br /> a Memory Game?
    </div>
    <DialogContent>
      <DialogContentText id="dialog-description">
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <div className='project-btn start' onClick={handleClose}>
        let's start!
        <span className='arrow'>
          <i class="fas fa-angle-right"></i>
        </span>
      </div>
      <div className='project-btn later' onClick={handleNotPlay}>
        maybe later
        <span className='arrow'>
          <svg xmlns="http://www.w3.org/2000/svg" width="22" height="22" fill="currentColor" class="bi bi-emoji-frown" viewBox="0 0 19 19">
            <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
            <path d="M4.285 12.433a.5.5 0 0 0 .683-.183A3.498 3.498 0 0 1 8 10.5c1.295 0 2.426.703 3.032 1.75a.5.5 0 0 0 .866-.5A4.498 4.498 0 0 0 8 9.5a4.5 4.5 0 0 0-3.898 2.25.5.5 0 0 0 .183.683zM7 6.5C7 7.328 6.552 8 6 8s-1-.672-1-1.5S5.448 5 6 5s1 .672 1 1.5zm4 0c0 .828-.448 1.5-1 1.5s-1-.672-1-1.5S9.448 5 10 5s1 .672 1 1.5z" />
          </svg>			</span>
      </div>
    </DialogActions>
  </div>
</Dialog>


  // <section id="contact" className='px-60 page' >
