import React, { Component } from 'react';
//import 'materialize-css/dist/css/materialize.min.css';


import './tetrisBgAnimation.css';
import { TETROMINOS } from '../tetris/tetrominos';

import CellPreview from '../tetris/components/CellPreview';


export default function TetrisBgAnimation(props) {


	return (
		<div  className='tetrisBgAnimation'  >
		   <div className='row1'>
                {['L','O','S','T','Z','I','J'].map((item,index)=>{
                  return  <CellPreview size={'50px'} key={index} type={item} text={TETROMINOS[item].text} shape={TETROMINOS[item].shape} />
                                }                )}
              </div>
			  <div className='row2'>
                {['L','O','S','T','Z','I','J'].map((item,index)=>{
                  return  <CellPreview key={index} type={item} text={TETROMINOS[item].text} shape={TETROMINOS[item].shape} />
                                }                )}
              </div>
		</div>
	);

}
