import React from 'react';
import { StyledStage } from '../styles/StyledStage';
import { TETROMINOS } from '../tetrominos';

import Cell from './Cell';

export default function Stage({ stage,newGame }) {
    const StageMain = () => (

        <StyledStage width={stage[0].length} height={stage.length} id="stage">
           {stage.map(row => row.map((cell, x) => {
           return <Cell key={x} type={cell[0]} text={cell[2]} />}))}
         </StyledStage>
     );
    const StageStartPositon = (  ) => {

        const newStage=stage;
        console.log(Object.entries(TETROMINOS))
        var y=1;
        Object.entries(TETROMINOS).forEach((obj , i)=>{
            obj[1].shape.forEach((value,x)=>{
                value.forEach((col,z)=>{
                    if (col !== 0) {
                        newStage[x+y ][z+2] = [
                          col,
                          'clear',
                          obj[1].text[x][z]
                        ];
                      }
                })

                });

                y=y+obj[1].shape.length;


        })
      return  <StyledStage width={stage[0].length} height={stage.length} id="stage-start">
          {newStage.map(row => row.map((cell, x) => {
          return <Cell key={x} type={cell[0]} text={cell[2]} />}))}
        </StyledStage>
          }
      ;
      if(newGame)
      return StageStartPositon()
      else   return StageMain()

}
