import React from 'react';
// import 'materialize-css/dist/css/materialize.min.css';
// import 'materialize-css/dist/js/materialize.min.js';
// import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Experiences from './components/pages/experiences/Experiences';
import Hero from './components/hero/hero';

import './App.css';

function App() {
	const [mobmenu, setMobmenu] = React.useState(false)
	React.useEffect(()=>{
		const appHeight = () => {
			const doc = document.documentElement

			doc.style.setProperty('--app-height', `${window.innerHeight}px`)
		}
		window.addEventListener('resize', appHeight)
		appHeight()
	},[])



      React.useEffect(() => {


            if(!mobmenu)
            document.getElementById("navbar-menu").classList.add('nav-scroll')

          },[mobmenu]);

  return (

    // <IndividualIntervalsExample/>
    // <Home active={active} isActive={isActive}/>
    <Router>
      <>
        <Route exact path="/" render={(props) =><Hero {...props} mobmenu={mobmenu} setMobmenu={(value)=>setMobmenu(value)}/>} />
        <Switch>
          <Route path="/work" render={(props) =><Experiences {...props} mobmenu={mobmenu} setMobmenu={(value)=>setMobmenu(value)} />} />

        </Switch>
      </>
    </Router>
  );
}

export default App;
