import styled from 'styled-components';

export const StyledCell = styled.div`
  width:  ${props => props.size}
  height:${props => (props.type === 0  ? '30px' : '30px')};
  // display:${props => (props.type === 0 ? 'none' : 'block')};
  /* border-radius: 5px; */
  background: rgba(${props => props.color}, 0.8);
//    border: ${props => (props.type === 0 ? '1px solid' : '0px solid rgba(0, 0, 0, 0.096)')};
//   border-bottom-color: rgba(0, 0, 0, 0.096);
//   border-right-color:  rgba(0, 0, 0, 0.096);
//   border-top-color:  rgba(0, 0, 0, 0.096);
//   border-left:  none;
border: ${props => (props.type === 0 ? '0px solid rgba(0, 0, 0, 0.096)' : '4px solid')};
border-bottom-color: rgba(${props => props.color}, 0.1);
border-right-color: rgba(${props => props.color}, 1);
border-top-color: rgba(${props => props.color}, 1);
border-left-color: rgba(${props => props.color}, 0.3);
content: "${props => props.text}";
`;
