import React from 'react';
//import 'materialize-css/dist/css/materialize.min.css';

import './hero.scss';
import ImgProfile from '../../images/me5.PNG';
import ImgProfileBG from '../../images/ofiice2.png';
import Draw from './draw';


export default function Hero(props) {
	React.useEffect(() => {
		var i = 1;

		const element = document.getElementById('hover-msg');

		element.addEventListener("animationend", () => {
			// Set your final state here. For example:
			element.style["opacity"] = 0;
		}, { once: true });
		// $('.load-jump').each(function() {
		// 	// $(this).removeClass('load-jump');
		// 	setTimeout(()=>{
		// 		$(this).removeClass('load-jump');

		// 		$(this).addClass('load-jump2');

		// 	},0+i)
		// 	setTimeout(()=>{
		// 		$(this).removeClass('load-jump2');

		// 	},1000+i)
		// 	i=i+200;
		// })
	}, [])

	const hoverLetter = () => {
		const element = document.getElementById('hover-msg');
		element.style["opacity"] = 0;

	}
	const drawCan = () => {
		const canvas = document.getElementById('drawing-board');
		// const toolbar = document.getElementById('toolbar');
		const ctx = canvas.getContext('2d');

		const canvasOffsetX = canvas.offsetLeft;
		const canvasOffsetY = canvas.offsetTop;

		canvas.width = window.innerWidth - canvasOffsetX;
		canvas.height = window.innerHeight - canvasOffsetY;
		ctx.strokeStyle = 'black';
		let isPainting = false;
		let lineWidth = 5;
		let startX;
		let startY;


		const draw = (e) => {
			if (!isPainting) {
				return;
			}

			ctx.lineWidth = 1;
			ctx.lineCap = 'round';

			ctx.lineTo(e.clientX - canvasOffsetX, e.clientY);
			ctx.stroke();
		}

		canvas.addEventListener('mousedown', (e) => {
			isPainting = true;
			startX = e.clientX;
			startY = e.clientY;
		});

		canvas.addEventListener('mouseup', e => {
			isPainting = false;
			ctx.stroke();
			ctx.beginPath();
		});

		canvas.addEventListener('mousemove', draw);

	}

	const startCornerHoverQ1 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(0.99,0,0.14,0.000,-0.2007,0.9,0.14,0,-0.13,-0.14,0.9,0,0,0,0,1);');

	}
	const EndCornerHoverQ1 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}
	const startCornerHoverQ2 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ2 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}

	const startCornerHoverQ3 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ3 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}
	const startCornerHoverQ4 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:matrix3d(1.1,0,0.00,0.0003,0.1,1,0,0,0,0,5,0,0,0,2,1);');

	}
	const EndCornerHoverQ4 = () => {
		document.getElementById('hover-bg-img').setAttribute('style', '--position:none;');

	}

	return (
		<div id={'hero-section-' + props.active + props.class} className={props.active === 0 ? 'hero-section active ' : 'hero-section'}  >

			<section id={'hero-' + props.active + props.class} className={props.active === 0 ? 'hero active ' : 'hero'}>
				<div className='content' >
					<div className='content-div'>
						<div className='max-div'>
							<div className=" clip-letter">	H<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='H' font="TikTok" /></div>
							<div className="clip clip-letter">I<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='I' font="TikTok" /></div>
							<span className='jump load-jump'>,</span>
						</div>

						<div className='hover-msg'>
							<div id="hover-msg" className="msg">Try Hover Letters!</div>
							<div className="clip clip-letter">I<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='I' font="TikTok" /></div>
							<span className='jump load-jump'>'</span>
							<div className="clip clip-letter">m<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='M' font="TikTok" /></div>
							{/* <div className='danielle'> */}
							{/* <span className='d-logo-x load-jump'>
					D
				</span> */}
							<span className=''> </span>


							<div className="clip clip-letter">D<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='D' font="TikTok" /></div>
							<div className="clip clip-letter">a<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='a' font="TikTok" /></div>
							<div className="clip clip-letter">N<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='N' font="TikTok" /></div>
							<div className="clip clip-letter">I<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='I' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='E' font="TikTok" /></div>
							<div className="clip clip-letter">L<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='L' font="TikTok" /></div>
							<div className="clip clip-letter">L<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='L' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='E' font="TikTok" /></div>
							<span className='jump load-jump txt-black'>,</span>
						</div>

						<div className='web-dev'>

							<div className="clip clip-letter">W<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='W' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='E' font="TikTok" /></div>
							<div className="clip clip-letter">B<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='B' font="TikTok" /></div>

							<span> </span>
							<div className="clip clip-letter">D<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='D' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='E' font="TikTok" /></div>
							<div className="clip clip-letter">V<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='V' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#CC99FF'} letter='E' font="TikTok" /></div>
							<div className="clip clip-letter">L<Draw callback={hoverLetter} lineColor={'#A9D1F7'} letter='L' font="TikTok" /></div>
							<div className="clip clip-letter">O<Draw callback={hoverLetter} lineColor={'#B4F0A7'} letter='O' font="TikTok" /></div>
							<div className="clip clip-letter">P<Draw callback={hoverLetter} lineColor={'#FFFFBF'} letter='P' font="TikTok" /></div>
							<div className="clip clip-letter">E<Draw callback={hoverLetter} lineColor={'#FFDFBE'} letter='E' font="TikTok" /></div>
							<div className="clip clip-letter">R<Draw callback={hoverLetter} lineColor={'#FFB1B0'} letter='R' font="TikTok" /></div>
						</div>
					</div>
					<span className='block'></span>


				</div>

				<div className='hero-img'>
				<img src={ImgProfile}></img>
				<div className='img-bg'>
					<div className='q q-1' onMouseEnter={startCornerHoverQ1} onMouseLeave={EndCornerHoverQ1}></div>
					<div className='q q-2' onMouseEnter={startCornerHoverQ2} onMouseLeave={EndCornerHoverQ2}></div>
					<div className='q q-3'></div>
					<div className='q q-4' onMouseEnter={startCornerHoverQ4} onMouseLeave={EndCornerHoverQ4}></div>

					<img src={ImgProfileBG} id='hover-bg-img'></img>
				</div>
			</div>
			</section>


		</div>
	);

}
