import React, { Component } from 'react';
import Hero from '../../components/hero/hero';
import HeroReverse from '../../components/hero/heroReverse';

import Carousel from 'react-bootstrap/Carousel';

import Navbar from '../../components/navbar/Navbar';
import $ from 'jquery';

import Profile from '../../components/pages/profile/Profile';
import About from '../../components/pages/about/About';
import ScrollableSection, { ScrollableLink } from 'react-update-url-on-scroll';
import { configureAnchors } from 'react-update-url-on-scroll'


function NoTransitionExample() {
  return (
    <Carousel slide={false}>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=First slide&bg=373940"
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Second slide&bg=282c34"
          alt="Second slide"
        />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Third slide&bg=20232a"
          alt="Third slide"
        />

        <Carousel.Caption>
          <h3>Third slide label</h3>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
}


export default function Home(props) {
	const [state, setState] = React.useState(true);
	const [active, isActive] = React.useState('hero');
	const [lastActive, setLastActive] = React.useState('hero');
	configureAnchors({scrollDelay: 100})


	// React.useEffect(()=>{

	// 		var $body    = $('html, body'), // Define jQuery collection 
	// 			content  = $('#main').smoothState({
	// 			  onStart : {
	// 				duration: 250,
	// 				render: function () {
	// 				  content.toggleAnimationClass('is-exiting');

	// 				  // Scroll user to the top
	// 				  $body.animate({ 'scrollTop': 0 });

	// 				}
	// 			  }
	// 			}).data('smoothState');

	// },[])
	function isInViewport(element) {
		const rect = element[0].getBoundingClientRect();
		if (rect.y > 0) rect.y = -1 * rect.y;

		return (
			rect.height + rect.y >= 0 && rect.height + rect.y <= 10)
	}
	function logit(e) {

		e.preventDefault();
		if (state) {
			setState(false);
			setTimeout(() => {
				setState(true);

			}, 1000);

			$('.scroll-container section').each(function () {
				if (isInViewport($(this))) {
					setLastActive(active)
					isActive($(this).attr('id'))
					return;
				}
			});
			document.getElementById('scroll-container').removeEventListener("scroll", logit);




		}

	}

	  React.useEffect(() => {

	    function watchScroll() {

	      document.getElementById('scroll-container').addEventListener("scroll", logit);
	    }
	    watchScroll();



	  },[state]);

	return (
		<div className="home">

			<Navbar active={active} { ...props} />
			<div id='scroll-container' onScroll={()=>{console.log('fff')}} className='scroll-container'>
			<Hero active={active} />
			<ScrollableSection hash={'#about'} >
				<div>
					<About active={active} isActive={isActive} />
				</div>
			</ScrollableSection>

			</div>

		</div>

	);
}
